type DataTableEmptyHeader<
  Row = DataTableRow,
> = {
  key:
    | DataTableKey<Row>
    | (string & {});
  /** Whether the header is empty */ empty: boolean;
  display?: (
    item: DataTableValue,
    row: Row,
  ) => DataTableValue;
  sort?:
    | false
    | ((
        a: DataTableValue,
        b: DataTableValue,
      ) => number);
  /** Whether the column menu is enabled */ columnMenu?: boolean;
  width?: string;
  minWidth?: string;
};

type DataTableNonEmptyHeader<
  Row = DataTableRow,
> = {
  key: DataTableKey<Row>;
  value: DataTableValue;
  display?: (
    item: DataTableValue,
    row: Row,
  ) => DataTableValue;
  sort?:
    | false
    | ((
        a: DataTableValue,
        b: DataTableValue,
      ) => number);
  /** Whether the column menu is enabled */ columnMenu?: boolean;
  width?: string;
  minWidth?: string;
};

interface DataTableRow<Id = any> {
  id: Id;
  [key: string]: DataTableValue;
}

type DataTableCell<Row = DataTableRow> =
  {
    key:
      | DataTableKey<Row>
      | (string & {});
    value: DataTableValue;
    display?: (
      item: DataTableValue,
      row: DataTableRow,
    ) => DataTableValue;
  };

